      class ObjMerger {
          static isMf(f) {
              return Utils.isFunction(f) && f.__mf;
          }
          static mf(f) {
              f.__mf = true;
              return f;
          }
          static merge(a, b) {
              const aks = Object.keys(a);
              const bks = Object.keys(b);
              if (!bks.length) {
                  return a;
              }
              const ai = {};
              const bi = {};
              for (let i = 0, n = bks.length; i < n; i++) {
                  const key = bks[i];
                  ai[key] = -1;
                  bi[key] = i;
              }
              for (let i = 0, n = aks.length; i < n; i++) {
                  const key = aks[i];
                  ai[key] = i;
                  if (bi[key] === undefined) {
                      bi[key] = -1;
                  }
              }
              const aksl = aks.length;
              const result = {};
              for (let i = 0, n = bks.length; i < n; i++) {
                  const key = bks[i];
                  const aIndex = ai[key];
                  let curIndex = aIndex;
                  while(--curIndex >= 0) {
                      const akey = aks[curIndex];
                      if (bi[akey] !== -1) {
                          break;
                      }
                  }
                  while(++curIndex < aIndex) {
                      const akey = aks[curIndex];
                      result[akey] = a[akey];
                  }
                  const bv = b[key];
                  const av = a[key];
                  let r;
                  if (this.isMf(bv)) {
                      r = bv(av);
                  } else {
                      if (!Utils.isObjectLiteral(av) || !Utils.isObjectLiteral(bv)) {
                          r = bv;
                      } else {
                          r = ObjMerger.merge(av, bv);
                      }
                  }
                  if (r !== undefined) {
                      result[key] = r;
                  }
              }
              let curIndex = aksl;
              while(--curIndex >= 0) {
                  const akey = aks[curIndex];
                  if (bi[akey] !== -1) {
                      break;
                  }
              }
              while(++curIndex < aksl) {
                  const akey = aks[curIndex];
                  result[akey] = a[akey];
              }
              return result;
          }
      }

